import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
//import {convertToRaw} from 'draft-js';
import {connect} from 'react-redux';
import {getUser} from './../../../reducers/user.reducer';
//import {getID} from './../../../reducers/repo.reducer';
import {newIssue} from './../../../actions/repo.actions';
import RichTextInput from '././../../global/components/richTextInput';


class NewIssuePage extends Component {
  constructor(props){
    super(props);
    this.state={
      text:"",
      title:"",
    }
    this.submitIssue=this.submitIssue.bind(this);
    this.handleChange=this.handleChange.bind(this);
    this.bindhandleTitleChange=this.handleTitleChange.bind(this);
  }

  submitIssue(){
    //alert(this.child.state);
    let data = this.child.getState().blocks[0].text;
    let title = this.state.title;
    this.props.dispatch(newIssue(title,data,this.props.match.params.id,"Imacunt",0,Date.now()));
  }
  handleChange(e){
    console.log(e.target);
    this.setState({text:e.target.value});
  }
  handleTitleChange(e){
    this.setState({title:e.target.value});
  }
  render() {
    console.log(this.props.user);
    return (
      <div className="NewIssuePage" style={{display:"flex", flexDirection:"column", alignItems:"center"}}>
        <div style={{width:"500px"}}>
          <div style={{textAlign:"center"}}>
            <h1>New Issue</h1>
          </div>
          <div className="NewIssueForm">
            <p>Issue Name</p>
            <input style={{width:"500px"}} value={this.state.title} onChange={(e)=>{this.handleTitleChange(e)}}/>
            <p>Comment</p>
            <RichTextInput ref={instance => { this.child = instance; }} onChange={this.handleChange} value={this.state.text}/>
            <div style={{display:"flex", justifyContent:"center"}}>
              <Button
                onClick={this.submitIssue} variant="outlined"
                style={{ fontFamily:"inherit", borderRadius:"25px", marginTop:"50px", marginBottom:"50px" }}
                >Submit</Button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps=(state)=>{
  return{
    user: getUser(state),
  }
}


export default connect(mapStateToProps)(NewIssuePage);
