import axios from 'axios'
import history from '../history'
import config from '../utils/config.js';



//ec2-35-153-131-45.compute-1.amazonaws.com
//// TODO: ADD A CONFIG FILE FOR ROUTES
export function logout(){
  localStorage.removeItem("user");
  return (dispatch) => {
      dispatch(signOut());
  }
}

export function signOut(){
  return{
    type: "LOGOUT",
  }
}

export function inviteCodeCheck(invite){
  return (dispatch)=>{
    axios.post(`http://${config.production_url}:8001/api/user/inviteCodeCheck`,invite).then(res => {
      if(res.data.result===true){
        sessionStorage.setItem('alpha',res.data.result);
        return dispatch(addInviteCode(res.data.result));
      }
      else {
        alert("Incorrect invite code");
        return dispatch(addInviteCode(res.data.result));
      }
    }).catch(err=>{console.log("Error",err); alert("Incorrect invite code")});
  }
};

export function addInviteCode(good){
  return{
    type: "GOOD_BINARY_HUMAN",
    good
  }
}


export function signin(username,password){
  return (dispatch) =>{
    axios.post(`http://${config.dev_url}:8001/api/user/login`,{
      username: username,
      password: password
    }).then(res => {
      console.log("res",res.data);
      history.push(`/source/${username}`);
      return dispatch(login(
        res.data.token,res.data.username,
        res.data.email,res.data.id,
        res.data.user))
  }).catch(err=>{console.log("Error",err); alert("Username or Password Incorrect")})
  }
}

export const login = (token,username,email,id,user) => {
  //return {type:'ERROR'}
  return{
    type: 'LOGIN',
    token,
    username,
    email,
    id,
    user
  }
}


export function fetchUser(){
  return (dispatch) => {
    return axios.post(`user`).then(res => {
      dispatch(addUser(res))
    })
  }
};

export function fetchUsers(){
  return (dispatch) => {
    return axios.post(`user`).then(res => {
      dispatch(addUser(res))
    })
  }
}


export function fetchUserRepos(){
  return(dispatch) =>{
    return axios.post(`user/repos`).then(res =>{
      dispatch(addRepos(res));
    })
  }
}

export function addUser(users){
  return {
    type: "ADD_USER",
    users
  }
}

export function addRepos(repos){
    return {
      type: "ADD_REPOS",
      repos
    }
}

export function protect(data){
  let tok = JSON.parse(localStorage.getItem("user"))
  axios.post('user/tsafe','post',{token: tok.data}).then(res => {console.log(res.message);})
}


export function handleStar(user,id,repo_id,src_percentage){
  return (dispatch) => {
    return (`user/${id}/star`,'post',{"repo_id":repo_id,"src_percentage": src_percentage}).then(res => {
      dispatch(fetchUsers(res));
    });
  }
}

export function newUser(data){
  return (dispatch)=>{
    axios.post(`http://${config.dev_url}:8001/api/user/newuser`,data).then(res => {
      if(!res.data.error){
        //console.log("RESPONSE", res.data)
        return dispatch(login(
          res.data.token,res.data.username,
          res.data.email,res.data.id,res.data.user))
      }else{
        //console.log("error",res.data.error);
        alert(res.data.error)
      }
    })
  }
};

export function updateUserStarred(starred){
  return{
    type: "UPDATE_STARS",
    starred
  }
}
